package data;

public class Hand {
	
	private float x;
	private float y;
	private float z;
	
	private boolean grab;


	public Hand() {
		super();
		// TODO Auto-generated constructor stub
	}
	

	public float getX() {
		return x;
	}

	public float getY() {
		return y;
	}

	public float getZ() {
		return z;
	}

	public boolean isGrab() {
		return grab;
	}
	

}
